import React from 'react';
import { useRef, useState, useEffect, useContext } from 'react';
import AuthContext from '../context/AuthContext';
import axios from 'axios';
import '../../App.css';
import './SignUpIn.css';

const LOGIN_URL = '/auth';

const SignIn = () => {
  const { setAuth } = useContext(AuthContext);
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState('');
  const [pwd, setPwd] = useState('');
  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, [])

  useEffect(() => {
    setErrMsg('');
  }, [user, pwd])

  const handleSubmit = async (e) => {
    e.preventDefault();

    try{
      const response = await axios.post(LOGIN_URL,
        JSON.stringify({user, pwd}),
        {
          headers: { 'Content-type': 'application/json' },
          withCredentials: true
        }
      );

      console.log(JSON.stringify(response?.data));
      const accessToken = response?.data?.accessToken;
      const roles = response?.data?.roles;
      setAuth({ user, pwd, roles, accessToken });
      setUser('');
      setPwd('');
      setSuccess(true);

    } catch (err) {
      if (!err?.response) {
        setErrMsg('No Serer Response');
      } else if (err.response?.status === 400) {
        serErrMsg('Missing Username or Password');
      } else if (err.response?.status === 401) {
        setErrMsg('Unauthorized');
      } else {
        setErrMsg('Login Failed');
      }
      errRef.current.focus();
    }
  }

  // render() {
  return (
    <>
    {success ? (
      <section>
        <h1>You are logged in!</h1>
        <br />
        <p>
          <a href='/'>Go to home</a>
        </p>
      </section>
    ) : (
      <div class='sign-in'>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <img src='' class="card-img" alt=''></img>
      <div class="img-overlay">
      <div class="main-block">
        <img
          src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
          alt="profile-img"
          className="profile-img-card"/>
        <form onSubmit={handleSubmit}>
          <label id="icon" for="Username"><i class="fas fa-user"></i></label>
          <input type='text' class="form-control" ref={userRef} placeholder="Username" onChange={(e) => setUser((e.target.value))} value={user} aria-label="Username" required/>
          <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
          <input type='password' class="form-control" placeholder="Password" onChange={(e) => setPwd((e.target.value))} value={pwd} aria-label="Password" required/>
          <p><a href=''>Forgot password?</a></p>
          <div class="btn-block">
            <p>By clicking Sign in, you agree on our <a href='/'>Privacy Policy</a>.</p>
            <button class="btn-sub" type="submit" href="/">Sign in</button>
          </div>
        </form>
      </div>
      </div>
    </div>
          )}
    </>
    );
  }
// }

export default SignIn;
