import React from 'react';
import '../../App.css'; 
import './SignUpIn.css';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";

export default class SignUp extends React.Component {
  constructor(props) {
    super(props);
    this.handleRegister = this.handleRegister.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.state = {
      username: "",
      email: "",
      password: "",
      successful: false,
      message: ""
    };
}

  onChangeUserName(e) {
    this.setState({
        username: e.target.value
      });
  }

  onChangeEmail(e) {
    this.setState({
        email: e.target.value
      });
  }

  onChangePassword(e) {
    this.setState({
        password: e.target.value
      });
  }

  handleRegister(e) {
    e.preventDefault();

    this.setState({
      message: "",
      successful: false
    });

    this.form.validateAll();

  if (this.checkBtn.context._errors.length === 0) {
    AuthService.register(
      this.state.username,
      this.state.email,
      this.state.password
    ).then(
      response => {
        this.setState({
          message: response.data.message,
          successful: true
        });
      },
      error => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        this.setState({
          successful: false,
          message: resMessage
        });
      }
    );
  }
}


  render() {
    return (
      <div className='sign-up'>
        <img src='' class="card-img" alt=''></img>
        <div class="img-overlay">
        <div class="main-block">
          <img
            src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
            alt="profile-img"
            className="profile-img-card"/>
        <form onSubmit={this.handleRegister} 
          ref={c => {
            this.form = c;
          }}>

             {!this.state.successful && (
              <div>
                <label id="icon" for="Username"><i class="fas fa-user"></i></label>
                <input 
                  type='text' 
                  class="form-control" 
                  placeholder="Username" 
                  aria-label="Username" 
                  value={this.state.username} 
                  onChange={this.onChangeUserName} 
                  validations={[required, username]} 
                  // required
                  />

                <label id="icon" for="Email"><i class="fas fa-envelope"></i></label>
                <input   
                  type='text' 
                  class="form-control" 
                  placeholder="Email" 
                  aria-label="Email"
                  value={this.state.email}
                  onChange={this.onChangeEmail} 
                  // required
                  />

                <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
                <input   
                  type='password' 
                  class="form-control" 
                  placeholder="Password" 
                  aria-label="Password"
                  value={this.state.password}
                  onChange={this.onChangePassword} 
                  // required
                  />

                <p>Already have an account?<a href="/sign-in"> Sign in</a> here.</p>
              <div class="btn-block">
                <p>By clicking Submit, you agree on our <a href='/'>Privacy Policy</a>.</p>
                <button class="btn-sub">Register</button>
              </div>
              </div>
             )}
        </form>
        </div>
        </div>
      </div>
    );
  }
}
