import React from 'react';
import axios from 'axios';
import '../../App.css';
import './SignUpIn.css';

export default class SignIn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      logged : false
    };
    console.log(this.props);
      this.onChangeUserName = this.onChangeUserName.bind(this);
      this.onChangePassword = this.onChangePassword.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
  }

  onChangeUserName(e) {
    this.setState({
        username: e.target.value
      });
}

  onChangePassword(e) {
    this.setState({
        password: e.target.value
      });
}

  async onSubmit(e) {
    e.preventDefault();
    // alert(this.state.username + " " + this.state.password);
    try {
      let res = await axios.post("/user/login", {
        username: this.state.username,
        password: this.state.password,
      });
      alert("Welcome, " + res.data.username);
      localStorage.setItem("user", res.data.username);
      localStorage.setItem("logged", "true");
      localStorage.setItem("role", res.data.authorities[0].authority.replace("ROLE_", ""));
      window.location.href="/";
      // let stateUsername = this.state.username;
      // this.props.history.push("/mata");
    } catch (err) {
      if (err.response.status === 401) {
          alert(err.response.data.message)
          window.location.reload();
      }
    } finally {
      //alert("stop");
  }
}

  render() {
    return (
      <div class='sign-in'>
        <img src='' class="card-img" alt=''></img>
        <div class="img-overlay">
        <div class="main-block">
          <img
            src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
            alt="profile-img"
            className="profile-img-card"/>
          <form onSubmit={this.onSubmit}>
            <label id="icon" for="Username"><i class="fas fa-user"></i></label>
            <input type='text' value={this.state.username} onChange={this.onChangeUserName} class="form-control" placeholder="Username" aria-label="Username" required/>
            <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
            <input type='password'  value={this.state.password} onChange={this.onChangePassword} class="form-control" placeholder="Password" aria-label="Password" required/>
            <p><a href="">Forgot password?</a></p>
          <div class="btn-block">
            <p>By clicking Submit, you agree on our <a href='/'>Privacy Policy</a>.</p>
            <button class="btn-sub" type="submit" href="/">Sign in</button>
          </div>
          </form>
        </div>
        </div>
      </div>
    );
  }
}
