import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import { Button } from './Button';
import './BtnNav.css';

function Navbar() {
  const {click, setClick} = useState(false);
  const{button, setButton} = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener('resize', showButton);

  return (
    <>
    <div class="collapse" id="navbarToggleExternalContent"></div>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to="/" className='navbar-logo' onClick={closeMobileMenu}>
            <img id='Logo' src="./logo.png" alt="Logo" width="120" height="65"/>
            {/* <i class='fab fa-typo3'/> */}
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
                </Link>
            </li>
            <li className='nav-item'>
              <Link to='/Summer 2022' className='nav-links' onClick={closeMobileMenu}>
                Summer 2022
                </Link>
            </li>
            <li className='nav-item'>
              <Link to='/Weekends' className='nav-links' onClick={closeMobileMenu}>
                Weekends
                </Link>
            </li>
            <li className='nav-item'>
              <Link to='/Sign in' className='nav-links-mobile' onClick={closeMobileMenu}>
                Sign in
                </Link>
            </li>
            <li className='nav-item'>
              <Link to='/Sign up' className='nav-links-mobile' onClick={closeMobileMenu}>
                Sign up
                </Link>
                {/* <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                 <button type="button" class="btn btn-outline-danger">Sign in</button>
                 <button type="button" class="btn btn-danger">Sign up</button>
               </div> */}
            </li>
          </ul>
          {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
        </div>
      </nav>
    </>
  )
}

export default Navbar
