import React from 'react';
import '../../App.css'; 
import './SignUpIn.css';
import axios from 'axios';
axios.defaults.withCredentials = true

export default class SignUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      email: "",
      password: ""
    };
    this.onChangeUserName = this.onChangeUserName.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
}

onChangeUserName(e) {
  this.setState({
      username: e.target.value
    });
}

onChangeEmail(e) {
  this.setState({
      email: e.target.value
    });
}

onChangePassword(e) {
  this.setState({
      password: e.target.value
    });
}

async onSubmit(e) {
  alert(this.state.username + " " + this.state.password);
  await axios.post("auth/signup", {
      username: this.state.username,
      password: this.state.password,
      email: this.state.email
  })
  .then((res) => {
      alert(res.status);
  })
  .catch((err) => {
      alert(err);
  })
}


  render() {
  return (
    <div className='sign-up'>
      <img src='' class="card-img" alt=''></img>
      <div class="img-overlay">
    <div class="main-block">
      <img
        src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
        alt="profile-img"
        className="profile-img-card"/>
    <form onSubmit={this.onSubmit}>
      <label id="icon" for="Username"><i class="fas fa-user"></i></label>
      <input value={this.state.username} onChange={this.onChangeUserName} type='text' class="form-control" placeholder="Username" aria-label="Username" required/>
      <label id="icon" for="Email"><i class="fas fa-envelope"></i></label>
      <input value={this.state.email} onChange={this.onChangeEmail} type='text' class="form-control" placeholder="Email" aria-label="Email" required/>
      <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
      <input value={this.state.password} onChange={this.onChangePassword} type='password' class="form-control" placeholder="Password" aria-label="Password" required/>
      <p>Already have an account?<a href="/sign-in"> Sign in</a> here.</p>
      <div class="btn-block">
        <p>By clicking Submit, you agree on our <a href='/'>Privacy Policy</a>.</p>
        <button type='submit' class="btn-sub">Register</button>
      </div>
    </form>
  </div>
    </div>
  </div>
  );
}
}
