import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import '../../App.css';
import './SignUpIn.css';

export default class SignIn extends React.Component  {


  render() {
  return (
      <div class='sign-in'>
      <img src='' class="card-img" alt=''></img>
      <div class="img-overlay">
      <div class="main-block">
        <img
          src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
          alt="profile-img"
          className="profile-img-card"/>
        <form onSubmit={handleSubmit}>
          <label id="icon" for="Username"><i class="fas fa-user"></i></label>
          <input type='text' class="form-control" ref={userRef} placeholder="Username" aria-label="Username" required/>
          <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
          <input type='password' class="form-control" placeholder="Password" aria-label="Password" required/>
          <p><a href=''>Forgot password?</a></p>
          <div class="btn-block">
            <p>By clicking Sign in, you agree on our <a href='/'>Privacy Policy</a>.</p>
            <button class="btn-sub" type="submit" href="/">Sign in</button>
          </div>
        </form>
      </div>
      </div>
    </div>
    );
  }
}
