import React from 'react';
import { useRef, useState, useEffect } from 'react';
import '../../App.css';
import './SignUpIn.css';

const SignIn = () => {
  const userRef = userRef();
  const errRef = useRef();

  const [user, setUser] = useState('');
  const [pwd, setPwd] = useState('');
  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, [])

  useEffect(() => {
    setErrMsg('');
  }, [user, pwd])

  // render() {
  return (
    <div class='sign-in'>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <img src='' class="card-img" alt=''></img>
      <div class="img-overlay">
      <div class="main-block">
        <img
          src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
          alt="profile-img"
          className="profile-img-card"/>
        <form>
          <label id="icon" for="Username"><i class="fas fa-user"></i></label>
          <input type='text' class="form-control" ref={userRef} placeholder="Username" onChange={(e) => setUser((e.target.value))} value={user} aria-label="Username" required/>
          <label id="icon" for="Password"><i class="fas fa-unlock-alt"></i></label>
          <input type='password' class="form-control" placeholder="Password" onChange={(e) => setPwd((e.target.value))} value={pwd} aria-label="Password" required/>
          <p><a href="/">Forgot password?</a></p>
          <div class="btn-block">
            <p>By clicking Submit, you agree on our <a href='/'>Privacy Policy</a>.</p>
            <button class="btn-sub" type="submit" href="/">Sign in</button>
          </div>
        </form>
      </div>
      </div>
    </div>
    );
  }
// }

export default SignIn;
